(function($){$.purr=function(notice,options){notice=$(notice);if(!options.isSticky){notice.addClass("not-sticky")}var cont=document.getElementById("purr-container");if(!cont){cont='<div id="purr-container"></div>'}cont=$(cont);$("body").append(cont);notify();function notify(){var close=document.createElement("a");$(close).attr({className:"close",href:"#close",innerHTML:"Close"}).appendTo(notice).click(function(){removeNotice();return false});notice.appendTo(cont).hide();if(jQuery.browser.msie&&options.usingTransparentPNG){notice.show()}else{notice.fadeIn(options.fadeInSpeed)}if(!options.isSticky){var topSpotInt=setInterval(function(){if(notice.prevAll(".not-sticky").length==0){clearInterval(topSpotInt);setTimeout(function(){removeNotice()},options.removeTimer)}},200)}}function removeNotice(){if(jQuery.browser.msie&&options.usingTransparentPNG){notice.css({opacity:0}).animate({height:"0px"},{duration:options.fadeOutSpeed,complete:function(){notice.remove()}})}else{notice.animate({opacity:"0"},{duration:options.fadeOutSpeed,complete:function(){notice.animate({height:"0px"},{duration:options.fadeOutSpeed,complete:function(){notice.remove()}})}})}}};$.fn.purr=function(options){options=options||{};options.fadeInSpeed=options.fadeInSpeed||500;options.fadeOutSpeed=options.fadeOutSpeed||500;options.removeTimer=options.removeTimer||4000;options.isSticky=options.isSticky||false;options.usingTransparentPNG=options.usingTransparentPNG||false;this.each(function(){new $.purr(this,options)});return this}})(jQuery);